INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (27, 'lead_gen_existing_customers_1', 'marketing', 'en_US', 'Talk to a consultant', 'As your business grows, you want to optimize costs, streamline operations, and sell more. We can help. Talk to us about how you can get the most out of WooCommerce.', '{}', 'unactioned', 'woocommerce.com', '2022-04-14 03:54:12', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (28, 'woocommerce-core-paypal-march-2022-updated', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.', '{}', 'pending', 'woocommerce.com', '2022-04-14 03:54:12', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (29, 'woocommerce-core-paypal-march-2022-updated-nopp', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).', '{}', 'pending', 'woocommerce.com', '2022-04-14 03:54:12', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (30, 'pinterest_03_2022_update', 'marketing', 'en_US', 'Your Pinterest for WooCommerce plugin is out of date!', 'Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on “update now” under Pinterest for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2022-04-14 03:54:12', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (31, 'wc-admin-navigation-nudge', 'info', 'en_US', 'You now have access to the WooCommerce navigation', 'We’re introducing a new navigation for a more intuitive and improved user experience. You can enable the beta version of the new experience in the Advanced Settings. Enable it now for your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-14 03:54:13', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (32, 'wc-admin-complete-store-details', 'info', 'en_US', 'Add your store details to complete store setup', 'Complete your store details with important information for setup such as your store’s base address', '{}', 'unactioned', 'woocommerce-admin', '2022-04-14 03:54:13', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (33, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2022-04-14 03:54:13', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (34, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-14 03:54:13', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (35, 'wc-admin-selling-online-courses', 'marketing', 'en_US', 'Do you want to sell online courses?', 'Online courses are a great solution for any business that can teach a new skill. Since courses don’t require physical product development or shipping, they’re affordable, fast to create, and can generate passive income for years to come. In this article, we provide you more information about selling courses using WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-14 03:55:31', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (36, 'gla-reconnect-wordpress', 'info', 'en_US', 'Re-connect your store to Google Listings & Ads', 'Your WordPress.com account has been disconnected from Google Listings &amp; Ads. Connect your WordPress.com account again to ensure your products stay listed on Google through the Google Listings &amp; Ads extension.<br /><br />If you do not re-connect, any existing listings may be removed from Google.', '{}', 'unactioned', 'gla', '2022-04-14 03:58:29', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (37, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-14 07:39:38', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (38, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-18 02:33:35', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (39, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Bảo vệ Của hàng WooCommerce của bạn bằng Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2022-04-18 02:33:35', NULL, 0, 'thumbnail', 'http://localhost:8888/zalopaystore/wp-content/plugins/woocommerce/packages/woocommerce-admin/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (40, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-18 02:33:35', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (41, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-18 02:33:35', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (42, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-04-18 02:33:35', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wp_zalopay_storewc_admin_notes` VALUES (43, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-04-18 02:33:35', NULL, 0, 'plain', '', 0, 0, 'info');
